struct S {
int res;
bool success;
};
int divide(int lhs, int rhs) { // без structured binding
if (rhs == 0) {
return ...; // непонятно какое значение возвращать
}
return lhs / rhs;
}
int divideWithStructuredBinding(int lhs, int rhs) {
if (rhs == 0) {
return {0, false}; // ошибка, первое значение можно
// игнорировать
}
return {lhs / rhs, true};
}
auto [res, success] = divideWithStructuredBinding(5, 0);
// res = 0, success = false
