function startListeningGestures(events) {
  const menuButtonTriggerArea = document.getElementById('menu-trigger');
  const menuListTriggerArea = document.querySelector('.slide-list');
  const contentArea = document.querySelector('.main-view');

  const menuManager = new TinyGesture(menuButtonTriggerArea, {});
  menuManager.on('tap', () => {
    events.menuOpen?.();
  });

  const menuListManager = new TinyGesture(menuListTriggerArea, {});
  menuListManager.on('swipeleft', () => {
    events.menuClose?.();
  });

  const contentManager = new TinyGesture(contentArea, {
    threshold: () => 10,
    velocityThreshold: 5,
  });
  contentManager.on('swiperight', () => {
    events.contentSwap?.('prev');
  });
  contentManager.on('swipeleft', () => {
    events.contentSwap?.('next');
  });
}


/**
 * MAIN
 */
const selectedColor = '#42f6db';

let currentLecture = Object.keys(slides)[0];

let currentSlides = slides[currentLecture];

let currentSlideName = Object.keys(currentSlides)[0];
let currentSlideItem;

let currentIndex = 0;

let currentTags = [];

let oldPath = document.location.href;

let loadSlide = (slideName) => {
  const newSlide = document.createElement('div');
  newSlide.innerHTML = currentSlides[slideName];
  document.getElementsByClassName('scrolling').item(0).appendChild(newSlide.childNodes[0]);

  currentSlideName = slideName;

  document.getElementsByClassName('pagination').item(0).innerText = currentIndex + 1;

  const tagsContainer = document.createElement('div');
  tagsContainer.classList.add('tags');

  currentTags = LECTURE_TAGS[currentLecture][currentSlideName] || [];
  currentTags.forEach((keyword) => {
    const tag = document.createElement('div');
    tag.innerText = keyword;
    tag.classList.add('tag');
    tagsContainer.appendChild(tag);
  });

  document.getElementsByClassName('scrolling').item(0).appendChild(tagsContainer);

  const menu = document.querySelector('.slide-list');
  if (menu.classList.contains('opened')) {
    menu.classList.remove('opened');
  }
}

let unloadCurrentSlide = () => {
  const slide = document.getElementsByClassName('scrolling').item(0);
  if (slide) {
    while (slide.firstChild) {
      slide.removeChild(slide.firstChild);
    }
  }
}

let createSlideElements = () => {
  const slideList = document.getElementsByClassName('slide-list').item(0);

  // create input search
  const searchContainer = document.createElement('div');
  const searchInput = document.createElement('input');
  searchInput.setAttribute('type', 'text');

  searchContainer.classList.add('search-container');
  searchInput.classList.add('search');
  searchInput.addEventListener('input', (event) => {
    const { value } = event.target;

    const dropdown = document.getElementsByClassName('dropdown').item(0);

    if (!dropdown) {
      console.error('No search dropdown');
      return;
    }

    dropdown.innerHTML = "";

    if (!value) {
      dropdown.classList.add('close');
      return;
    }

    const results = searchInTags(value);

    const mappedResults = Object.entries(results).map(([lecture, slides]) => Object.entries(slides).map(([slide, tags]) => [`${lecture}: ${slide}`, tags])).flat();

    if (!mappedResults.length) {
      const item = document.createElement('div');
      item.classList.add('dropdown-item');
      item.classList.add('not-found-item');
      item.innerText = 'Ничего не найдено';
      dropdown.appendChild(item);
    } else {
      mappedResults.forEach(([found, tags]) => {
        const item = document.createElement('div');
        item.classList.add('dropdown-item');

        const title = document.createElement('div');
        title.innerText = found;

        const tagsContainer = document.createElement('div');
        tagsContainer.classList.add('search-tags-container');
        tags.forEach((tagTitle) => {
          const tag = document.createElement('div');
          tag.innerHTML = tagTitle;
          tag.classList.add('search-tag');
          tagsContainer.appendChild(tag);
        });

        item.appendChild(title);
        item.appendChild(tagsContainer);

        item.addEventListener('click', (event) => {
          if (currentLecture) {
            document.getElementById(currentLecture).classList.remove('open');

            document.getElementById(currentLecture).nextSibling.classList.remove('slides-open');
            
            unloadCurrentSlide();
            selectInSlideList(currentSlideItem, false);
          }

          const split = found.split(':');

          currentLecture = split[0].trim();
          currentSlides = slides[currentLecture];
          currentSlideName = split[1].trim();
          currentIndex = Object.keys(currentSlides).indexOf(currentSlideName);

          document.getElementById(currentLecture).classList.add('open');

          loadSlide(currentSlideName);
          currentSlideItem = document.getElementById(Object.keys(currentSlides)[currentIndex]);
          selectInSlideList(currentSlideItem);
          
          document.getElementById(currentLecture).nextSibling.classList.add('slides-open');

          dropdown.classList.add('close');
          searchInput.value = "";

          history.pushState(null, null, currentSlideName);
        });

        dropdown.appendChild(item);
      });
    }   

    if (dropdown.classList.contains('close') && value) {
      dropdown.classList.remove('close');
    }
  });
  searchContainer.appendChild(searchInput);

  const searchIcon = document.createElement('div');
  searchIcon.classList.add('clear-icon');
  searchContainer.appendChild(searchIcon);

  slideList.appendChild(searchContainer);

  // create slides list
  Object.entries(slides).forEach(([lecture, slides]) => {
    slideList.appendChild(createSlideTitle(lecture));

    const itemsContainer = document.createElement('div');
    itemsContainer.classList.add('slides');
    Object.keys(slides).forEach((slide) => itemsContainer.appendChild(createSlideItem(slide)));

    slideList.appendChild(itemsContainer);
  });
}

const createSlideTitle = (title) => {
  const container = document.createElement('div');
  container.classList.add('slide-container', 'clickable');
  container.id = title;

  const textContainer = document.createElement('div');
  textContainer.classList.add('slide-element-text', 'slide-element-title');
  const text = document.createTextNode(title);
  textContainer.appendChild(text);

  container.appendChild(textContainer);

  container.addEventListener('click', (event) => {
    const target = event.target.closest('.clickable').nextSibling;
    if (!target) {
      console.error('Cannot find container to toggle');
    }
    
    document.getElementById(currentLecture).classList.remove('open');

    document.getElementById(currentLecture).nextSibling.classList.remove('slides-open');
  
    unloadCurrentSlide();
    selectInSlideList(currentSlideItem, false);

    currentLecture = title;
    currentSlides = slides[currentLecture];
    currentSlideName = Object.keys(currentSlides)[0];
    currentIndex = 0;

    document.getElementById(currentLecture).classList.add('open');

    loadSlide(currentSlideName);
    currentSlideItem = document.getElementById(Object.keys(currentSlides)[currentIndex]);
    selectInSlideList(currentSlideItem);

    target.classList.add('slides-open');

    history.pushState(null, null, currentSlideName);
  });

  return container;
}

let createSlideItem = (slideName) => {
  const slideContainer = document.createElement('div');
  slideContainer.classList.add('slide-container');
  slideContainer.id = slideName;

  const slideElement = document.createElement('div');
  slideElement.classList.add('slide-element');

  const slideElementText = document.createElement('div');
  slideElementText.classList.add('slide-element-text');
  const text = document.createTextNode(slideName);
  slideElementText.appendChild(text);

  slideElement.appendChild(slideElementText);
  slideContainer.appendChild(slideElement);

  slideContainer.addEventListener('click', (event) => {
    selectInSlideList(currentSlideItem, false);
    unloadCurrentSlide();

    currentSlideItem = event.currentTarget;
    selectInSlideList(currentSlideItem);

    currentIndex = Object.keys(currentSlides).indexOf(event.currentTarget.id);

    loadSlide(currentSlideItem.id);

    history.pushState(null, null, currentSlideItem.id);
  });

  return slideContainer;
}

let selectInSlideList = (slideItem, select = true) => {
  if (!slideItem) {
    return;
  }
  if (select) {
    slideItem.style.background = selectedColor;
  } else {
    slideItem.style.removeProperty('background');
  }
}

function updateSlide(index) {
  if (index >= Object.keys(currentSlides).length || index <= -1) {
    return;
  }

  currentIndex = index;

  selectInSlideList(currentSlideItem, false);
  unloadCurrentSlide();

  const newSlideName = Object.keys(currentSlides).at(currentIndex);

  loadSlide(newSlideName);

  currentSlideItem = document.getElementById(newSlideName);
  selectInSlideList(currentSlideItem);
  currentSlideItem.scrollIntoView(false);

  history.pushState(null, null, newSlideName);
}

function searchInTags(filter) {
  const searchResults = {};

  Object.entries(LECTURE_TAGS).forEach(([lecture, slideTags]) => {
    Object.entries(slideTags).forEach(([slideName, tags]) => {
      if (tags.some(slideTag => slideTag.toLowerCase().includes(filter.toLowerCase()))) {
        if (!searchResults[lecture]) {
          searchResults[lecture] = {};
        }
        searchResults[lecture][slideName] = tags.map((tag) => {
          const lowerFilter = filter.toLowerCase();
          const lowerTag = tag.toLowerCase();

          if (lowerTag.includes(lowerFilter)) {
            return lowerTag.replace(lowerFilter, `<span>${lowerFilter}</span>`);
          } else {
            return lowerTag;
          }
        });
      }
    });
  });

  return searchResults;
}

function createDropdown() {
  const container = document.createElement('div');
  container.classList.add('dropdown');
  container.classList.add('close');

  document.body.appendChild(container);
}


function initSlideFromPath() {
  if (currentLecture) {
    document.getElementById(currentLecture).classList.remove('open');

    document.getElementById(currentLecture).nextSibling.classList.remove('slides-open');
    
    unloadCurrentSlide();
    selectInSlideList(currentSlideItem, false);
  }

  const slideName = window.location.pathname.replace('/', '');

  const result = Object.entries(slides).find(([lecture, slideNames]) => {
    return Object.keys(slideNames).includes(slideName);
  });

  if (result) {
    const [lecture,] = result;

    currentLecture = lecture;
    currentSlides = slides[currentLecture];
    currentSlideName = slideName;
    currentIndex = Object.keys(currentSlides).indexOf(currentSlideName);

    document.getElementById(currentLecture).classList.add('open');

    loadSlide(currentSlideName);
    currentSlideItem = document.getElementById(Object.keys(currentSlides)[currentIndex]);
    selectInSlideList(currentSlideItem);

    document.getElementById(currentLecture).nextSibling.classList.add('slides-open');
  }
}

window.onload = () => {
  // navigation
  window.addEventListener('popstate', function() {
    initSlideFromPath();
  });

  // create app
  const scrollingContainer = document.createElement('div');
  scrollingContainer.classList.add('scrolling');
  document.getElementsByClassName('main-view').item(0).appendChild(scrollingContainer);

  createSlideElements();
  createDropdown();

  document.addEventListener('keydown', (event) => {
      if (event.isComposing || event.key === 'ArrowDown' || event.key === 'ArrowRight') {
        updateSlide(currentIndex + 1);
      }

    if (event.isComposing || event.key === 'ArrowUp' || event.key === 'ArrowLeft') {
      updateSlide(currentIndex - 1);
    }
  });

  startListeningGestures({
    menuOpen: () => {
      const menu = document.querySelector('.slide-list');
      menu.classList.add('opened');
    },
    menuClose: () => {
      const menu = document.querySelector('.slide-list');
      if (menu.classList.contains('opened')) {
        menu.classList.remove('opened');
      }
    },
    contentSwap: (direction) => {
      switch (direction) {
        case 'next': 
          updateSlide(currentIndex + 1);
          break;
        case 'prev': 
          updateSlide(currentIndex - 1);
          break;
      }
    },
  });

  if (window.location.pathname) {
    initSlideFromPath();
  }
}
